// SPDX-License-Identifier: MIT
// FeverTokens Contracts v1.0.0

pragma solidity ^0.8.20;

import { IERC20Snapshot } from "./IERC20Snapshot.sol";
import { ERC20SnapshotStorage, ERC20SnapshotInternal } from "./ERC20SnapshotInternal.sol";
import { Math } from "../../../utils/Math.sol";

/**
 * @title ERC20 base implementation with support for token balance and supply snapshots
 */
abstract contract ERC20Snapshot is IERC20Snapshot, ERC20SnapshotInternal {
    /// @inheritdoc IERC20Snapshot
    function balanceOfAt(
        address account,
        uint256 snapshotId
    ) public view returns (uint256) {
        return _balanceOfAt(account, snapshotId);
    }

    /// @inheritdoc IERC20Snapshot
    function totalSupplyAt(uint256 snapshotId) public view returns (uint256) {
        return _totalSupplyAt(snapshotId);
    }
}

// SPDX-License-Identifier: MIT
// FeverTokens Contracts v1.0.0

pragma solidity ^0.8.20;

import { IERC20SnapshotInternal } from "./IERC20SnapshotInternal.sol";
import { ERC20Base, ERC20BaseInternal } from "../base/ERC20Base.sol";
import { ERC20SnapshotStorage } from "./ERC20SnapshotStorage.sol";
import { Math } from "../../../utils/Math.sol";

/**
 * @title ERC20Snapshot internal functions
 */
abstract contract ERC20SnapshotInternal is IERC20SnapshotInternal, ERC20Base {
    function _balanceOfAt(
        address account,
        uint256 snapshotId
    ) internal view returns (uint256) {
        (bool snapshotted, uint256 value) = _valueAt(
            snapshotId,
            ERC20SnapshotStorage.layout().accountBalanceSnapshots[account]
        );
        return snapshotted ? value : _balanceOf(account);
    }

    function _valueAt(
        uint256 snapshotId,
        ERC20SnapshotStorage.Snapshots storage snapshots
    ) private view returns (bool, uint256) {
        if (snapshotId == 0) revert ERC20Snapshot__SnapshotIdIsZero();
        ERC20SnapshotStorage.Layout storage l = ERC20SnapshotStorage.layout();

        if (snapshotId > l.snapshotId)
            revert ERC20Snapshot__SnapshotIdDoesNotExists();

        uint256 index = _findUpperBound(snapshots.ids, snapshotId);

        if (index == snapshots.ids.length) {
            return (false, 0);
        } else {
            return (true, snapshots.values[index]);
        }
    }

    /**
     * @notice find index of first element of array that is greater than or equal to given query
     * @dev array must be sorted in ascending order and contain no duplicates
     * @dev derived from https://github.com/OpenZeppelin/openzeppelin-contracts (MIT license)
     * @param array array to query
     * @param query element to search for
     * @return index of query or array length if query is not found or exceeded
     */
    function _findUpperBound(
        uint256[] storage array,
        uint256 query
    ) private view returns (uint256) {
        unchecked {
            if (array.length == 0) {
                return 0;
            }

            uint256 low = 0;
            uint256 high = array.length;

            while (low < high) {
                uint256 mid = Math.average(low, high);

                if (array[mid] > query) {
                    high = mid;
                } else {
                    low = mid + 1;
                }
            }

            return (low > 0 && array[low - 1] == query) ? low - 1 : low;
        }
    }

    function _totalSupplyAt(
        uint256 snapshotId
    ) internal view returns (uint256) {
        (bool snapshotted, uint256 value) = _valueAt(
            snapshotId,
            ERC20SnapshotStorage.layout().totalSupplySnapshots
        );
        return snapshotted ? value : _totalSupply();
    }

    function _snapshot() internal virtual returns (uint256) {
        ERC20SnapshotStorage.Layout storage l = ERC20SnapshotStorage.layout();

        l.snapshotId++;

        uint256 current = l.snapshotId;
        emit Snapshot(current);
        return current;
    }

    function _updateAccountSnapshot(address account) private {
        _updateSnapshot(
            ERC20SnapshotStorage.layout().accountBalanceSnapshots[account],
            _balanceOf(account)
        );
    }

    function _updateTotalSupplySnapshot() private {
        _updateSnapshot(
            ERC20SnapshotStorage.layout().totalSupplySnapshots,
            _totalSupply()
        );
    }

    function _updateSnapshot(
        ERC20SnapshotStorage.Snapshots storage snapshots,
        uint256 value
    ) private {
        uint256 current = ERC20SnapshotStorage.layout().snapshotId;

        if (_lastSnapshotId(snapshots.ids) < current) {
            snapshots.ids.push(current);
            snapshots.values.push(value);
        }
    }

    function _lastSnapshotId(
        uint256[] storage ids
    ) private view returns (uint256) {
        return ids.length == 0 ? 0 : ids[ids.length - 1];
    }

    /**
     * @notice ERC20 hook: update snapshot data
     * @inheritdoc ERC20BaseInternal
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);

        if (from == address(0)) {
            _updateAccountSnapshot(to);
            _updateTotalSupplySnapshot();
        } else if (to == address(0)) {
            _updateAccountSnapshot(from);
            _updateTotalSupplySnapshot();
        } else {
            _updateAccountSnapshot(to);
            _updateAccountSnapshot(from);
        }
    }
}

// SPDX-License-Identifier: MIT
// FeverTokens Contracts v1.0.0

pragma solidity ^0.8.20;

library ERC20SnapshotStorage {
    struct Snapshots {
        uint256[] ids;
        uint256[] values;
    }

    struct Layout {
        mapping(address => Snapshots) accountBalanceSnapshots;
        Snapshots totalSupplySnapshots;
        uint256 snapshotId;
    }

    bytes32 internal constant STORAGE_SLOT =
        keccak256("fevertokens.contracts.storage.ERC20Snapshot");

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

